import React, { Component } from 'react';
import logo from '../assets/images/logo.png';
import { baseUrl } from '../components/BaseUrl';
import Swal from "sweetalert2";

class Todos extends Component {

  constructor(props){
    super(props);
    this.state = {
      tasks: [],
      item: "",
      isAddingItem: false,
      postsPerPage: 10,
      currentPage: 1,
    }
  }

  checkToken = () => {
    if(!localStorage.getItem("token")){
      this.props.history.push("/login")
    }
  }

  logout = () => {
    if(localStorage.clear()) {
      this.props.history.push("/login")
    }
  }

  componentDidMount(){
    this.checkToken();
    this.getTodoList()
  }

  getTodoList = async () => {
    let obj = {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    };
    await fetch(`${baseUrl}todoApp/tasks`, obj)
      .then((response) => response.json())
      .then((responseJson) => {
        console.warn(responseJson);
        if (responseJson) {
            this.setState({ tasks: responseJson });
          }else{
            Swal.fire({
              title: "Error!",
              text: "Could not retrieve todo list. Please try again later",
              icon: "error",
              confirmButtonText: "OK",
            })
          }
      })
      .catch((error) => {
        Swal.fire({
          title: "Error!",
          text: error.message,
          icon: "error",
          confirmButtonText: "OK",
        });
      });
  }

  showTasks = () => {
    const { postsPerPage, currentPage, tasks } = this.state;
    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = parseInt(indexOfLastPost) - parseInt(postsPerPage);
    const currentPosts = tasks.slice(indexOfFirstPost, indexOfLastPost);
    try {
      return currentPosts.map((item, index) => {
        return (
        <div class="container col-md-12">
        <div style={{display:'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
        <li class="list-group-item col form-control ">{`${item.title}`}
        <i class="fas fa-male text-info mx-2"></i>
        </li>
        </div>
        </div>
            );
      });
    } catch (e) {
      // Swal.fire({
      //   title: "Error",
      //   text: e.message,
      //   type: "error",
      // })
    }
  }


  createTodo = async () => {
    const { item } = this.state;
    this.setState({isAddingItem: true, isDisabled: true})
    let req = {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title: `${item}`,
        description: "hello",
        completed: false,
      }),
    };
    await fetch(`${baseUrl}todoApp/tasks`, req)
      .then((response) => response.json())
      .then((responseJson) => {
        // console.warn(responseJson);
        if(responseJson){
          this.setState({isAddingItem: false, isDisabled: false})
          Swal.fire({
            title: "Success",
            text: 'Task added successfully!',
            icon: "success",
            confirmButtonText: "OK",
          })
        }else{
          this.setState({isAddingItem: false, isDisabled: false})
          Swal.fire({
            title: "Error!",
            text: 'An error occurred. Please try again later.',
            icon: "error",
            confirmButtonText: "OK",
          });
        }
      })
      .catch((error) => {
        this.setState({isAddingItem: false, isDisabled: false})
        Swal.fire({
          title: "Error!",
          text: error.message,
          icon: "error",
          confirmButtonText: "OK",
        });
      })
     }


  render(){
    const { isAddingItem } = this.state;
    return(
      <div className="container">
      <div className="text-center">
        <img src={logo} className="img-fluid profile-image-pic img-thumbnail my-3"
          width="200px" alt="logo" />
      </div>
      <div className="info-container">
      <div className="user-name">
      <h2>Hello {`${localStorage.getItem("name")}`}</h2>
      </div>
      <div className="user-name mb-2">
      <button className="btn btn-danger" onClick={(e) => this.logout()} style={{ fontSize: 18 }}>Logout</button>
      </div>
      </div>
      <div className="todo-container">
      <div className="todo-app">
        <form className="add-todo-form">
          <input className="add-todo-input" type="text" placeholder="Add item" onChange={(e) =>
            this.setState({ item: e.target.value })
          } />
          <button type="button" onClick={(e) => this.createTodo()} className="add-todo-btn">
          {isAddingItem ? (
            'adding item ...'
          ) : (
            "Add"
          )}
          </button>
        </form>
        <div class="row">
          <div class="col-12 col-md-6">
            <h6 class="text-muted">List Group</h6>
              <ul class="list-group">
        {this.showTasks()}
        </ul>
        </div>
        </div>
      </div>
      </div>
      </div>
    )
  }
}

export default Todos;
